def alphabeta(values, depth, index, maximizing, alpha, beta):
    if depth == 0 or index >= len(values):
        return values[index]
    if maximizing:
        best = float('-inf')
        for i in range(2):
            val = alphabeta(values, depth - 1, index * 2 + i, False, alpha, beta)
            best = max(best, val)
            alpha = max(alpha, best)
            if beta <= alpha:
                break
        return best
    else:
        best = float('inf')
        for i in range(2):
            val = alphabeta(values, depth - 1, index * 2 + i, True, alpha, beta)
            best = min(best, val)
            beta = min(beta, best)
            if beta <= alpha:
                break
        return best


