def is_safe(board, row, col):
    for c in range(col):
        if board[c] == row:
            return False
        if abs(board[c] - row) == abs(c - col):
            return False
    return True


def solve_n_queens(n):
    board = [-1] * n
    solutions = []

    def backtrack(col):
        if col == n:
            solutions.append(board.copy())
            return
        for row in range(n):
            if is_safe(board, row, col):
                board[col] = row
                backtrack(col + 1)
                board[col] = -1

    backtrack(0)
    return solutions


