def exps_to_polybits(exps):
    m = max(exps)
    bits = ['0'] * (m + 1)
    for e in exps:
        bits[m - e] = '1'
    return ''.join(bits)

def xor_divide(data, poly):
    n = len(poly)
    reg = list(data + '0'*(n-1))
    for i in range(len(data)):
        if reg[i] == '1':
            for j in range(n):
                reg[i+j] = '1' if reg[i+j] != poly[j] else '0'
    return ''.join(reg[-(n-1):]) if n>1 else ''

