def dijkstra(nodes, g, src):
    dist = {n: float('inf') for n in nodes}
    prev = {n: None for n in nodes}
    dist[src] = 0
    pq = [(0, src)]
    while pq:
        d,u = heapq.heappop(pq)
        if d>dist[u]: continue
        for v,w in g[u]:
            nd = d + w
            if nd < dist[v]:
                dist[v] = nd
                prev[v] = u
                heapq.heappush(pq, (nd, v))
    return dist, prev

def path(prev, dest):
    if prev[dest] is None:
        return [dest]
    p = []
    cur = dest
    while cur is not None:
        p.append(cur)
        cur = prev[cur]
    return p[::-1]

