def bfs_tree(nodes, g, root):
    parent = {root: None}
    level = {root:0}
    q = deque([root])
    while q:
        u = q.popleft()
        for v in sorted(g[u]):
            if v not in parent:
                parent[v] = u
                level[v] = level[u]+1
                q.append(v)
    return parent, level

