        g[b][a] = w
    return nodes, g

def init(nodes, g):
    t = {}
    for u in nodes:
        t[u] = {}
        for v in nodes:
            if u==v: t[u][v] = (0,u)
            elif v in g[u]: t[u][v] = (g[u][v], v)
            else: t[u][v] = (INF, None)
    return t

def step(nodes, g, tables):
    changed = False
    msgs = []
    for u in nodes:
        dv = {d:tables[u][d][0] for d in tables[u]}
        for nbr in g[u]:
            msgs.append((nbr,u,dv))
    for to, frm, dv in msgs:
        cost = g[to].get(frm, INF)
        if cost>=INF: continue
        for dest,cost_if in dv.items():
            if cost_if>=INF: continue
            nc = cost + cost_if
            cur, nxt = tables[to][dest]
            if nc < cur:
                tables[to][dest] = (nc, frm)
                changed = True
    return changed

def print_tables(nodes, tables):
