#include <Wire.h>
#include <LiquidCrystal_I2C.h>

int enA = 5;
int in1 = 3;
int in2 = 4;
LiquidCrystal_I2C lcd(32,16,2);

int tempPin = A1;
int lightPin = A2;
int moisturePin = A0;

void setup() {
  lcd.begin(16,2);
  lcd.init();
  lcd.backlight();
  Serial.begin(9600);
  pinMode(enA, OUTPUT);
  pinMode(in1, OUTPUT);
  pinMode(in2, OUTPUT);
}

void loop() {
  int tempValue = analogRead(tempPin);
  int lightValue = analogRead(lightPin);
  int moistureValue = analogRead(moisturePin);

  float temp = (((tempValue * 5.0) / 1024.0)-0.5)*100;
  float light = (lightValue * 5.0) / 1024.0;
  float moisture = (moistureValue * 5.0) / 1024.0;

  Serial.print("Temp "); Serial.print(temp); Serial.println(" C");
  Serial.print("Light:"); Serial.print(light); Serial.println("V");
  Serial.print("Moist: "); Serial.print(moisture); Serial.println("V");

  lcd.clear();
  lcd.setCursor(0, 0);
  lcd.print("Temp:"); lcd.print(temp); lcd.print("C");
  lcd.setCursor(0, 1);
  lcd.print("Light:"); lcd.print(light); lcd.print("V");
  lcd.setCursor(8, 1);
  lcd.print("Moist:"); lcd.print(moisture); lcd.print("V");

  if((temp > 30 || light < 2)&& moisture < 4 ) {
    digitalWrite(in1, HIGH);
    digitalWrite(in2, LOW);
    analogWrite(enA, 255);
    Serial.println("Turning on the motor...");
  } else {
    digitalWrite(in1, LOW);
    digitalWrite(in2, LOW);
    analogWrite(enA, 0);
    Serial.println("Turning off the motor...");
  }
  delay(1000);
}

#include <Servo.h>
Servo servo;
const int PIRpin=7;
const int buzzerPin=6;
const int ledPin=5;
const int blue=10;
const int green=11;
const int red=8;
const int temperaturePin = A0;
const int gas_pin = A1;
const int LEFT = 4, RIGHT=3, UP=2,DOWN=1;

void setup() {
  servo.attach(9);
  servo.write(0);
  pinMode(PIRpin, INPUT);
  pinMode (temperaturePin , INPUT);
  pinMode (gas_pin, INPUT);
  pinMode(LEFT, INPUT);
  pinMode(RIGHT, INPUT);
  pinMode(UP, INPUT);
  pinMode(DOWN, INPUT);
  pinMode(buzzerPin, OUTPUT);
  pinMode(ledPin, OUTPUT);
  pinMode(blue, OUTPUT);
  pinMode(green, OUTPUT);
  Serial.begin(9600);
}

void loop() {
  float degreesC;
  degreesC=analogRead(0);
  degreesC=degreesC*0.0048828125;
  degreesC=(degreesC-0.5)*100;

  int sensorValue;
  sensorValue = analogRead(gas_pin);
  int right,left,up,down;
  right = digitalRead(RIGHT);
  left = digitalRead(LEFT);
  up = digitalRead(UP);
  down = digitalRead(DOWN);

  if((right == LOW && left == LOW)||(up == LOW && down == LOW)&& sensorValue > 720 && degreesC>37) {
    digitalWrite(red,HIGH); delay(500);
    digitalWrite(red,LOW); delay(500);
    digitalWrite(red,HIGH); delay(500);
    digitalWrite(red,LOW); delay(500);
    Serial.println("Accident Detected");
    Serial.println("Sending Alert");
    delay(1000);
  }
  else if( sensorValue > 720 && degreesC>37) {
    tone(buzzerPin, 1000, 500);
    delay(1000);
    digitalWrite(blue,HIGH); delay(500);
    digitalWrite(blue,LOW); delay(500);
    digitalWrite(blue,HIGH); delay(500);
    digitalWrite(blue,LOW); delay(500);
    Serial.println("Gas is detected And Teperature Is Increased:");
    delay(10000);
  }
  else if(degreesC>37) {
    tone(buzzerPin, 1000, 200);
    delay(1000);
    digitalWrite(green,HIGH); delay(500);
    digitalWrite(green,LOW);
    digitalWrite(green,HIGH); delay(500);
    digitalWrite(green,LOW); delay(500);
    Serial.println("Teperature Is Increased:");
    delay(10000);
  }
  else {
    noTone(buzzerPin);
  }

  if(digitalRead(PIRpin)==1) {
    tone(buzzerPin, 1000, 2000);
    delay(1000);
    digitalWrite(ledPin,HIGH); delay(500);
    digitalWrite(ledPin,LOW); delay(500);
    digitalWrite(ledPin,HIGH); delay(500);
    digitalWrite(ledPin,LOW); delay(500);
    servo.write(90);
    delay(5000);
    servo.write(0);
  }
}

#include <LiquidCrystal.h>
const int temp_trans_pin = A0;
const int heater_pin     = 13;
const int fan_pin        = 6;

float MinTemp = 20;
float MaxTemp = 25;

LiquidCrystal lcd(12, 11, 5, 4, 3, 2);

void setup() {
  lcd.begin(16, 2);
  pinMode(heater_pin, OUTPUT);
  pinMode(fan_pin, OUTPUT);

  lcd.clear();
  lcd.print("Room Temp (C):");
  lcd.setCursor(2, 1);
  lcd.print(MinTemp);
  lcd.print("-");
  lcd.print(MaxTemp);
  delay(2000);
}

void loop() {
  int raw = analogRead(temp_trans_pin);
  float Equ_volt = raw * 5.0 / 1023.0;
  float SensorTemp = 100.0 * Equ_volt - 50.0;

  lcd.clear();
  lcd.print("Sensor: ");
  lcd.print(SensorTemp);
  lcd.print(" C");
  delay(1500);

  if (SensorTemp >= MaxTemp) {
    digitalWrite(heater_pin, LOW);
    analogWrite(fan_pin, 255);
    lcd.clear();
    lcd.print("Temp is HIGH");
    lcd.setCursor(0, 1);
    lcd.print("Turn ON FAN");
    delay(2000);
  }
  else if (SensorTemp <= MinTemp) {
    analogWrite(fan_pin, 0);
    digitalWrite(heater_pin, HIGH);
    lcd.clear();
    lcd.print("Temp is LOW");
    lcd.setCursor(0, 1);
    lcd.print("Turn ON HEATER");
    delay(2000);
  }
  else {
    analogWrite(fan_pin, 0);
    digitalWrite(heater_pin, LOW);
    lcd.clear();
    lcd.print("Temp is NORMAL");
    lcd.setCursor(0, 1);
    lcd.print("Turn OFF all");
    delay(2000);
  }
  delay(500);
}
