// Techused: Arduino, DHT11 Temperature and Humidity Sensor, 16x2 LCD Display

#include <LiquidCrystal.h>
#include "DHT.h"

#define DHTPIN 2     // Pin where the DHT11 data pin is connected
#define DHTTYPE DHT11   // Define the type of DHT sensor

// Initialize the DHT sensor
DHT dht(DHTPIN, DHTTYPE);

// Initialize the LCD library with the numbers of the interface pins
LiquidCrystal lcd(12, 11, 5, 4, 3, 7);

void setup() {
  // Set up the LCD's number of columns and rows
  lcd.begin(16, 2);
  // Initialize DHT sensor
  dht.begin();

  lcd.print("Weather Station");
  delay(1500);
  lcd.clear();
}

void loop() {
  // Wait a few seconds between measurements.
  delay(2000);

  // Reading temperature or humidity takes about 250 milliseconds!
  float humidity = dht.readHumidity();
  // Read temperature as Celsius (the default)
  float tempC = dht.readTemperature();

  // Check if any reads failed and exit early (to try again).
  if (isnan(humidity) || isnan(tempC)) {
    lcd.clear();
    lcd.print("Failed to read");
    lcd.setCursor(0, 1);
    lcd.print("from DHT sensor!");
    return;
  }

  // Clear the LCD
  lcd.clear();

  // Display temperature
  lcd.setCursor(0, 0);
  lcd.print("Temp: ");
  lcd.print(tempC);
  lcd.print((char)223); // Degree symbol
  lcd.print("C");

  // Display humidity
  lcd.setCursor(0, 1);
  lcd.print("Humidity: ");
  lcd.print(humidity);
  lcd.print("%");
}

// Techused: Arduino, Ultrasonic Distance Sensor, 16x2 LCD Display

#include <LiquidCrystal.h>

// Define pins for Ultrasonic Sensor
const int trigPin = 9;
const int echoPin = 10;

// Define LCD pins
LiquidCrystal lcd(12, 11, 5, 4, 3, 2);

// Define tank dimensions (in cm)
const float tankHeight = 100.0; // Assuming a 1-meter high tank
const float tankCapacity = 600.0; // Litres

void setup() {
  lcd.begin(16, 2);
  pinMode(trigPin, OUTPUT);
  pinMode(echoPin, INPUT);
  lcd.print("Water Level");
  lcd.setCursor(0, 1);
  lcd.print("Indicator");
  delay(2000);
}

void loop() {
  long duration, distance;

  // Get distance reading from sensor
  digitalWrite(trigPin, LOW);
  delayMicroseconds(2);
  digitalWrite(trigPin, HIGH);
  delayMicroseconds(10);
  digitalWrite(trigPin, LOW);
  duration = pulseIn(echoPin, HIGH);

  // Calculate distance in cm
  distance = duration * 0.034 / 2;

  // Calculate water level and volume
  float waterLevelCm = tankHeight - distance;
  if (waterLevelCm < 0) {
    waterLevelCm = 0;
  }
  float waterPercentage = (waterLevelCm / tankHeight) * 100;
  float waterVolume = (waterPercentage / 100) * tankCapacity;

  // Display on LCD
  lcd.clear();
  lcd.setCursor(0, 0);
  lcd.print("Level: ");
  lcd.print(waterPercentage, 1); // 1 decimal place
  lcd.print("%");

  lcd.setCursor(0, 1);
  lcd.print("Volume: ");
  lcd.print(waterVolume, 1);
  lcd.print(" L");

  delay(1000);
}

// Techused: Arduino, Soil Moisture Sensor, DC Motor (as water pump), Relay Module (optional)

// Define pin for the soil moisture sensor (Analog pin)
const int moisturePin = A0;

// Define pin for the water pump (Digital pin)
const int pumpPin = 7;

// Set the threshold for soil moisture. This value may need calibration.
// Lower value means drier soil.
const int dryThreshold = 500;

void setup() {
  Serial.begin(9600);
  pinMode(pumpPin, OUTPUT);
  // Make sure pump is off initially
  digitalWrite(pumpPin, LOW);
  Serial.println("Automatic Plant Watering System");
}

void loop() {
  // Read the soil moisture value
  int moistureValue = analogRead(moisturePin);

  Serial.print("Soil Moisture Reading: ");
  Serial.println(moistureValue);

  // Check if the soil is dry
  if (moistureValue > dryThreshold) {
    Serial.println("Soil is dry. Turning pump ON.");
    digitalWrite(pumpPin, HIGH); // Turn the pump on
    delay(2000); // Water for 2 seconds
    digitalWrite(pumpPin, LOW);  // Turn the pump off
    Serial.println("Pump turned OFF. Waiting...");
    // Wait for a while before checking again to allow water to seep in
    delay(10000); 
  } else {
    Serial.println("Soil is moist. Pump remains OFF.");
    digitalWrite(pumpPin, LOW); // Ensure pump is off
  }

  // Wait before the next reading
  delay(5000);
}

// Techused: Arduino, 4x4 Keypad, Servo Motor, LCD Display

#include <Keypad.h>
#include <Servo.h>
#include <LiquidCrystal.h>

const byte ROWS = 4;
const byte COLS = 4;
char keys[ROWS][COLS] = {
  {'1','2','3','A'},
  {'4','5','6','B'},
  {'7','8','9','C'},
  {'*','0','#','D'}
};
byte rowPins[ROWS] = {9, 8, 7, 6};
byte colPins[COLS] = {5, 4, 3, 2};

Keypad customKeypad = Keypad(makeKeymap(keys), rowPins, colPins, ROWS, COLS);
Servo myServo;
LiquidCrystal lcd(13, 12, 11, 10, A0, A1);

String password = "1234";
String inputPassword = "";

void setup(){
  myServo.attach(A2);
  lcd.begin(16, 2);
  lockDoor();
}

void loop(){
  char customKey = customKeypad.getKey();

  if (customKey){
    if (customKey == '*'){
      inputPassword = "";
      lcd.clear();
      lcd.print("Enter Password:");
    } else if (customKey == '#') {
      checkPassword();
    } else {
      inputPassword += customKey;
      lcd.setCursor(inputPassword.length() - 1, 1);
      lcd.print("*");
    }
  }
}

void checkPassword(){
  if (inputPassword == password){
    unlockDoor();
  } else {
    lcd.clear();
    lcd.print("Wrong Password!");
    delay(1000);
    lockDoor();
  }
  inputPassword = "";
}

void lockDoor(){
  myServo.write(0); // Locked position
  lcd.clear();
  lcd.print("Door is Locked");
  lcd.setCursor(0,1);
  lcd.print("Enter Password:");
}

void unlockDoor(){
  myServo.write(90); // Unlocked position
  lcd.clear();
  lcd.print("Door is Open");
  delay(5000); // Keep door open for 5 seconds
  lockDoor();
}

// Techused: Arduino, Ultrasonic Sensor, Servo Motor

#include <Servo.h>

Servo myServo;

const int trigPin = 9;
const int echoPin = 10;
const int servoPin = 11;

// define distance threshold in cm for opening the lid
const int openDistance = 20; 

void setup() {
  myServo.attach(servoPin);
  pinMode(trigPin, OUTPUT);
  pinMode(echoPin, INPUT);
  myServo.write(0); // Start with lid closed
  Serial.begin(9600);
}

void loop() {
  long duration, distance;

  // Trigger the sensor
  digitalWrite(trigPin, LOW);
  delayMicroseconds(2);
  digitalWrite(trigPin, HIGH);
  delayMicroseconds(10);
  digitalWrite(trigPin, LOW);

  // Read the echo
  duration = pulseIn(echoPin, HIGH);

  // Calculate the distance
  distance = duration * 0.034 / 2;

  Serial.print("Distance: ");
  Serial.println(distance);

  // Check if an object is within range
  if (distance < openDistance) {
    Serial.println("Object detected! Opening lid.");
    myServo.write(90); // Open the lid
    delay(5000);       // Keep it open for 5 seconds
    Serial.println("Closing lid.");
    myServo.write(0);  // Close the lid
    delay(1000);       // Wait a bit before next detection
  } else {
    myServo.write(0); // Ensure lid is closed
  }

  delay(100);
}

// Techused: Arduino, Flame Sensor, Buzzer, LED

const int flamePin = 2;   // Digital pin for flame sensor
const int buzzerPin = 8;  // Pin for the buzzer
const int ledPin = 13;    // On-board LED or an external one

void setup() {
  pinMode(flamePin, INPUT);
  pinMode(buzzerPin, OUTPUT);
  pinMode(ledPin, OUTPUT);
  Serial.begin(9600);
  Serial.println("Fire Detection System Initialized");
}

void loop() {
  // Read the state of the flame sensor
  int flameState = digitalRead(flamePin);

  // The flame sensor output is LOW when flame is detected
  if (flameState == LOW) {
    Serial.println("FIRE DETECTED! Activating alarm.");
    // Activate alarm
    digitalWrite(buzzerPin, HIGH);
    digitalWrite(ledPin, HIGH);
    delay(100); // Buzzer and LED on for a short duration
    digitalWrite(buzzerPin, LOW);
    digitalWrite(ledPin, LOW);
    delay(100); // Off for a short duration to create a beeping/flashing effect
  } else {
    Serial.println("No fire detected. All clear.");
    // Deactivate alarm
    digitalWrite(buzzerPin, LOW);
    digitalWrite(ledPin, LOW);
  }

  // Wait for a second before the next check
  delay(1000);
}

// Techused: Arduino, Soil Moisture Sensor, LDR (Photoresistor), Temp Sensor (TMP36), Relay, DC Motor

// Pins
const int moisturePin = A0;
const int ldrPin = A1;
const int tempPin = A2;
const int pumpPin = 7;

// Thresholds (adjust based on your sensors and needs)
const int drySoilThreshold = 600;  // Higher value means drier
const int hotTempThreshold = 30;   // in Celsius
const int brightLightThreshold = 700; // Higher value means brighter light

void setup() {
  Serial.begin(9600);
  pinMode(pumpPin, OUTPUT);
  digitalWrite(pumpPin, LOW); // Pump off initially
  Serial.println("Smart Irrigation System Activated");
}

void loop() {
  // Read sensor values
  int moistureValue = analogRead(moisturePin);
  int ldrValue = analogRead(ldrPin);

  // Convert TMP36 reading to Celsius
  int tempReading = analogRead(tempPin);
  float voltage = tempReading * 5.0 / 1024.0;
  float tempC = (voltage - 0.5) * 100;

  Serial.print("Moisture: "); Serial.print(moistureValue);
  Serial.print(" | Temp: "); Serial.print(tempC);
  Serial.print(" C | Light: "); Serial.println(ldrValue);

  // Decision logic
  bool shouldWater = false;

  if (moistureValue > drySoilThreshold) {
    Serial.println("Reason: Soil is dry.");
    // Don't water if it's too bright (daytime) to avoid evaporation
    if (ldrValue < brightLightThreshold) {
       shouldWater = true;
    } else {
      Serial.println("Info: Too bright, will wait for evening.");
    }
  } else if (tempC > hotTempThreshold) {
    Serial.println("Reason: It's very hot. Cooling the soil.");
     if (ldrValue < brightLightThreshold) {
       shouldWater = true;
    } else {
      Serial.println("Info: Too bright, will wait for evening.");
    }
  }

  // Activate pump if needed
  if (shouldWater) {
    Serial.println("-> ACTION: Turning pump ON for 5 seconds.");
    digitalWrite(pumpPin, HIGH);
    delay(5000); // Water for 5 seconds
    digitalWrite(pumpPin, LOW);
    Serial.println("-> ACTION: Pump OFF. Waiting before next cycle.");
    delay(20000); // Wait 20 seconds before next check
  } else {
    Serial.println("-> STATUS: Soil is moist. No watering needed.");
  }

  delay(5000); // Check conditions every 5 seconds
}

// Techused: Arduino, MPU6050 (Accelerometer/Gyro), MQ-2 Gas Sensor, TMP36 Temp Sensor, Buzzer, LEDs

#include <Wire.h>
#include <MPU6050.h>

MPU6050 mpu;

// Pins
const int gasPin = A0;
const int tempPin = A1;
const int buzzerPin = 8;
const int redLedPin = 7;   // For accident alert
const int yellowLedPin = 6; // For gas/temp alert

// Thresholds
const int gasThreshold = 400;     // Detects smoke/flammable gas
const int tempThreshold = 45;      // High temperature in Celsius
const int accidentThreshold = 18;  // High G-force for accident detection

void setup() {
  Serial.begin(9600);
  Wire.begin();
  mpu.initialize();
  Serial.println(mpu.testConnection() ? "MPU6050 connection successful" : "MPU6050 connection failed");

  pinMode(buzzerPin, OUTPUT);
  pinMode(redLedPin, OUTPUT);
  pinMode(yellowLedPin, OUTPUT);
}

void loop() {
  // 1. Accident Detection
  Vector rawAccel = mpu.getAcceleration();
  float totalAccel = sqrt(pow(rawAccel.XAxis, 2) + pow(rawAccel.YAxis, 2) + pow(rawAccel.ZAxis, 2)) / 16384.0; // Convert to G's

  if (totalAccel > accidentThreshold) {
    Serial.println("!!! ACCIDENT DETECTED !!!");
    triggerAlarm(redLedPin, 500); // Fast beep
  }

  // 2. Gas Leak Detection
  int gasValue = analogRead(gasPin);
  if (gasValue > gasThreshold) {
    Serial.println("WARNING: Gas Leak Detected!");
    triggerAlarm(yellowLedPin, 1000); // Slower beep
  }

  // 3. High Temperature Detection
  int tempReading = analogRead(tempPin);
  float tempC = (tempReading * 5.0 / 1024.0 - 0.5) * 100;
  if (tempC > tempThreshold) {
    Serial.println("WARNING: High Temperature Detected!");
    triggerAlarm(yellowLedPin, 1000); // Slower beep
  }

  Serial.print("Accel(G): "); Serial.print(totalAccel, 2);
  Serial.print(" | Gas: "); Serial.print(gasValue);
  Serial.print(" | Temp(C): "); Serial.println(tempC, 1);

  delay(500);
}

void triggerAlarm(int ledPin, int beepInterval) {
  digitalWrite(ledPin, HIGH);
  digitalWrite(buzzerPin, HIGH);
  delay(beepInterval);
  digitalWrite(ledPin, LOW);
  digitalWrite(buzzerPin, LOW);
  delay(beepInterval);
}

// Techused: Arduino, Temperature Sensor (TMP36), LCD, Relay, Fan (DC Motor), Heater (Resistor + LED)

#include <LiquidCrystal.h>

// Pins
const int tempPin = A0;
const int fanPin = 7;      // Cooling system
const int heaterPin = 8;   // Heating system

LiquidCrystal lcd(12, 11, 5, 4, 3, 2);

// Temperature setpoints
const float setPoint = 25.0; // Desired temperature
const float tolerance = 2.0;   // Hysteresis range (+/-)

const float upperThreshold = setPoint + tolerance;
const float lowerThreshold = setPoint - tolerance;

void setup() {
  lcd.begin(16, 2);
  pinMode(fanPin, OUTPUT);
  pinMode(heaterPin, OUTPUT);
  digitalWrite(fanPin, LOW);
  digitalWrite(heaterPin, LOW);

  lcd.print("Temp Controller");
  delay(1500);
}

void loop() {
  // Read temperature
  int tempReading = analogRead(tempPin);
  float voltage = tempReading * 5.0 / 1024.0;
  float currentTemp = (voltage - 0.5) * 100.0;

  // Display information
  lcd.clear();
  lcd.setCursor(0, 0);
  lcd.print("Set: "); lcd.print(setPoint, 1);
  lcd.print("C  Curr: "); lcd.print(currentTemp, 1);
  lcd.print("C");

  // Control logic
  lcd.setCursor(0, 1);
  if (currentTemp > upperThreshold) {
    // Temperature is too high, turn on fan
    digitalWrite(fanPin, HIGH);
    digitalWrite(heaterPin, LOW);
    lcd.print("Status: COOLING");
  } else if (currentTemp < lowerThreshold) {
    // Temperature is too low, turn on heater
    digitalWrite(fanPin, LOW);
    digitalWrite(heaterPin, HIGH);
    lcd.print("Status: HEATING");
  } else {
    // Temperature is within tolerance, do nothing
    digitalWrite(fanPin, LOW);
    digitalWrite(heaterPin, LOW);
    lcd.print("Status: STABLE");
  }

  delay(1000);
}

// Techused: Arduino, ECG Sensor (simulated via Analog input), LCD Display, Serial Plotter

#include <LiquidCrystal.h>

// Pins
const int ecgPin = A0;   // Analog pin to read ECG signal
const int buzzerPin = 8; // Buzzer for alerts

LiquidCrystal lcd(12, 11, 5, 4, 3, 2);

// BPM calculation variables
unsigned long lastBeatTime = 0;
const int heartRateThreshold = 600; // Adjust based on sensor output for detecting a beat
bool beatDetected = false;
int bpm = 0;

void setup() {
  Serial.begin(9600); 
  lcd.begin(16, 2);
  pinMode(buzzerPin, OUTPUT);
  lcd.print("Portable ECG");
  lcd.setCursor(0, 1);
  lcd.print("Initializing...");
  delay(2000);
}

void loop() {
  int ecgValue = analogRead(ecgPin);

  // Simple peak detection for BPM calculation
  if (ecgValue > heartRateThreshold && !beatDetected) {
    unsigned long currentTime = millis();
    unsigned long timeSinceLastBeat = currentTime - lastBeatTime;
    if (timeSinceLastBeat > 250) { // Debounce to avoid multiple detections for one beat
      bpm = 60000 / timeSinceLastBeat;
      lastBeatTime = currentTime;
      beatDetected = true;
      digitalWrite(buzzerPin, HIGH); // Beep on beat
      delay(10);
      digitalWrite(buzzerPin, LOW);
    }
  } else if (ecgValue < heartRateThreshold) {
    beatDetected = false;
  }

  // Display on LCD
  lcd.clear();
  lcd.setCursor(0, 0);
  lcd.print("ECG Signal: ");
  lcd.print(ecgValue);

  lcd.setCursor(0, 1);
  lcd.print("Heart Rate: ");
  if (bpm > 30 && bpm < 200) { // Filter out unrealistic values
      lcd.print(bpm);
      lcd.print(" BPM");
  } else {
      lcd.print("-- BPM");
  }

  // Send data to Serial Plotter to visualize the waveform
  Serial.println(ecgValue);

  delay(50);
}
